DROP TABLE xnjia_mysalf.cb_sys_user;
CREATE TABLE xnjia_mysalf.cb_sys_user (ROW_ID int NOT NULL AUTO_INCREMENT, SORT_NO int(10) NOT NULL COMMENT '序列号,用于排序', USER_CODE varchar(32) NOT NULL COMMENT '用户编码', USER_PASSWORD varchar(500) NOT NULL COMMENT '用户密码', USER_NAME varchar(32) NOT NULL COMMENT '用户名', COMPANY_ID int(10) NOT NULL COMMENT '公司ID', NOTES varchar(200) COMMENT '备注', CREATE_TIME datetime DEFAULT CURRENT_TIMESTAMP NOT NULL COMMENT '创建时间', PRIMARY KEY (ROW_ID)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='用户表';


DROP TABLE xnjia_mysalf.cb_sys_user_role;
CREATE TABLE xnjia_mysalf.cb_sys_user_role (ROW_ID int NOT NULL AUTO_INCREMENT, USER_ID int(10) NOT NULL COMMENT '用户Id', ROLE_ID int(10) NOT NULL COMMENT '角色权限', NOTES varchar(200) COMMENT '备注', CREATE_TIME datetime DEFAULT CURRENT_TIMESTAMP NOT NULL COMMENT '创建时间', PRIMARY KEY (ROW_ID)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='用户表';


DROP TABLE xnjia_mysalf.cb_sys_role;
CREATE TABLE xnjia_mysalf.cb_sys_role (ROW_ID int NOT NULL AUTO_INCREMENT, ROLE_NAME varchar(32) NOT NULL COMMENT '权限', NOTES varchar(200) COMMENT '备注', CREATE_TIME datetime DEFAULT CURRENT_TIMESTAMP NOT NULL COMMENT '创建时间', PRIMARY KEY (ROW_ID)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='权限表';
